INSERT INTO `articles` (`ArticleID`, `Title`, `Description`, `Content`, `Img`, `Day`, `Time`, `CategoryID`, `UserID`, `Status`, `Popular`) VALUES
(20, 'Hoa hậu Hương Giang kín đáo vẫn gợi cảm với váy công sở', 'Các set đồ váy sơ mi thời thượng, váy liền.', '<p style=\"text-align: justify;\">Hương Giang kh&ocirc;ng chỉ g&acirc;y ấn tượng l&agrave; một trong những hoa hậu c&oacute; đời tư &ldquo;sạch sẽ&rdquo; nhất, m&agrave; c&ocirc; c&ograve;n lu&ocirc;n tạo được thiện cảm bằng vẻ đẹp ph&uacute;c hậu, nụ cười tươi tắn. Phong c&aacute;ch ăn mặc của người đẹp cũng nhận được nhiều lời khen ngợi v&igrave; lu&ocirc;n c&acirc;n bằng được n&eacute;t thanh lịch với sự gợi cảm. Trong bộ ảnh thời trang mới nhất, hoa hậu Hương Giang xuống phố rạng rỡ với trang phục nữ t&iacute;nh. C&aacute;c set đồ v&aacute;y sơ mi thời thượng, v&aacute;y liền ngắn trẻ trung, hay đầm liền chiết eo cao gợi cảm đều rất ph&ugrave; hợp với phom d&aacute;ng cơ thể của Hương Giang.</p>\r\n<p style=\"text-align: center;\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-19/1513654929-477-tony4742-1513654606-width500height750.jpg\" alt=\"Hoa hậu Hương Giang k&iacute;n đ&aacute;o vẫn gợi cảm với v&aacute;y c&ocirc;ng sở - 2\" /></p>\r\n<p style=\"text-align: center;\">V&aacute;y giả vest chiết eo cao gi&uacute;p t&ocirc;n l&ecirc;n đ&ocirc;i ch&acirc;n d&agrave;i</p>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<div class=\"banner_inread\" style=\"text-align: center;\">&nbsp;</div>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<p style=\"text-align: center;\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-19/1513654929-387-tony4762-1513654606-width500height750.jpg\" alt=\"Hoa hậu Hương Giang k&iacute;n đ&aacute;o vẫn gợi cảm với v&aacute;y c&ocirc;ng sở - 3\" /></p>\r\n<p style=\"text-align: center;\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-19/1513654929-639-tony4765-1513654606-width500height750.jpg\" alt=\"Hoa hậu Hương Giang k&iacute;n đ&aacute;o vẫn gợi cảm với v&aacute;y c&ocirc;ng sở - 4\" /></p>\r\n<p style=\"text-align: center;\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-19/1513654929-376-tony4780-1513654606-width500height750.jpg\" alt=\"Hoa hậu Hương Giang k&iacute;n đ&aacute;o vẫn gợi cảm với v&aacute;y c&ocirc;ng sở - 5\" /></p>\r\n<p style=\"text-align: center;\">V&aacute;y sơ mi kẻ sọc phối m&agrave;u nổi bật x&oacute;a tan đi kiểu d&aacute;ng tưởng như cơ bản</p>\r\n<p style=\"text-align: center;\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-19/1513654930-568-tony4803-1513654606-width500height750.jpg\" alt=\"Hoa hậu Hương Giang k&iacute;n đ&aacute;o vẫn gợi cảm với v&aacute;y c&ocirc;ng sở - 6\" /></p>\r\n<p style=\"text-align: center;\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-19/1513654930-574-tony4816-1513654606-width500height750.jpg\" alt=\"Hoa hậu Hương Giang k&iacute;n đ&aacute;o vẫn gợi cảm với v&aacute;y c&ocirc;ng sở - 7\" /></p>\r\n<p style=\"text-align: center;\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-19/1513654930-728-tony4830-1513654606-width500height750.jpg\" alt=\"Hoa hậu Hương Giang k&iacute;n đ&aacute;o vẫn gợi cảm với v&aacute;y c&ocirc;ng sở - 8\" /></p>\r\n<p style=\"text-align: center;\">Một sự kết hợp đầm qu&acirc;y với &aacute;o sơ mi rất thời thượng</p>\r\n<p style=\"text-align: center;\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-19/1513654930-341-tony4843-1513654606-width500height750.jpg\" alt=\"Hoa hậu Hương Giang k&iacute;n đ&aacute;o vẫn gợi cảm với v&aacute;y c&ocirc;ng sở - 9\" /></p>\r\n<p style=\"text-align: center;\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-19/1513654930-821-tony4849-1513654606-width500height750.jpg\" alt=\"Hoa hậu Hương Giang k&iacute;n đ&aacute;o vẫn gợi cảm với v&aacute;y c&ocirc;ng sở - 10\" /></p>\r\n<p style=\"text-align: center;\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-19/1513654930-552-tony4881-1513654606-width500height750.jpg\" alt=\"Hoa hậu Hương Giang k&iacute;n đ&aacute;o vẫn gợi cảm với v&aacute;y c&ocirc;ng sở - 11\" /></p>\r\n<p style=\"text-align: center;\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-19/1513654930-318-tony4884-1513654606-width500height750.jpg\" alt=\"Hoa hậu Hương Giang k&iacute;n đ&aacute;o vẫn gợi cảm với v&aacute;y c&ocirc;ng sở - 12\" /></p>\r\n<p style=\"text-align: center;\">Set đồ n&agrave;y l&agrave; một sự lựa chọn ho&agrave;n hảo cho c&aacute;c c&ocirc; n&agrave;ng c&ocirc;ng sở điệu đ&agrave;</p>', 'thoitrang.jpg', '20-05-2024', '15:42:27', 1, 1, 1, 0),
(21, '\"Bạn gái Noo Phước Thịnh\" mặc váy cưới 3 tỷ đồng', 'Á hậu Tú Anh khoe vẻ đẹp gợi cảm ', '<p align=\"center\"><img class=\"news-image\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-19/1513653098-247-t---anh6-1513650264-width660height709.jpg\" alt=\"&amp;#34;Bạn g&aacute;i Noo Phước Thịnh&amp;#34; mặc v&aacute;y cưới 3 tỷ đồng - 1\" /></p>\r\n<p style=\"text-align: center;\">T&uacute; Anh xuất hiện quyến rũ trong thiết kế v&aacute;y cưới tiền tỷ</p>\r\n<p style=\"text-align: justify;\">Lu&ocirc;n xuất hiện y&ecirc;u kiều v&agrave; xinh đẹp trong những bộ ảnh thời trang, mới đ&acirc;y &Aacute; hậu t&uacute; anh&nbsp;g&acirc;y cho&aacute;ng ngợp khi h&oacute;a&nbsp;th&agrave;nh n&agrave;ng c&ocirc;ng ch&uacute;a lộng lẫy với bộ v&aacute;y cưới xa hoa c&oacute; trị gi&aacute; tới 3 tỷ đồng. Được biết, chiếc v&aacute;y cưới đặc biệt n&agrave;y gắn 20 kim cương nhập khẩu của Swarovski. Ngo&agrave;i ra, phần đu&ocirc;i v&aacute;y trải d&agrave;i với hơn 5.000 c&aacute;nh hoa th&ecirc;u v&agrave; hơn 6.000 vi&ecirc;n pha l&ecirc; Swarovski lớn nhỏ kh&aacute;c. Việc&nbsp;đ&iacute;nh số kim cương v&agrave; pha l&ecirc; n&agrave;y l&ecirc;n chiếc v&aacute;y đ&atilde; ngốn hết 3.600 giờ l&agrave;m việc của nhiều&nbsp;thợ th&ecirc;u thủ c&ocirc;ng t&agrave;i năng.</p>\r\n<p style=\"text-align: center;\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-19/1513653099-597-tu-anh1-1513649618-width660height478.jpg\" alt=\"&amp;#34;Bạn g&aacute;i Noo Phước Thịnh&amp;#34; mặc v&aacute;y cưới 3 tỷ đồng - 2\" /></p>\r\n<p style=\"text-align: center;\" align=\"center\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-19/1513653099-644-tu-anh4-1513650225-width660height562.jpg\" alt=\"&amp;#34;Bạn g&aacute;i Noo Phước Thịnh&amp;#34; mặc v&aacute;y cưới 3 tỷ đồng - 3\" /></p>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<div class=\"banner_inread\" style=\"text-align: center;\">&nbsp;</div>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<p style=\"text-align: center;\">Chiếc v&aacute;y thiết kế đu&ocirc;i d&agrave;i lộng lẫy</p>\r\n<p style=\"text-align: justify;\">Phần cổ v&aacute;y được chế t&aacute;c từ v&agrave;ng trắng, gắn kim cương v&ocirc; c&ugrave;ng rực rỡ. Lớp l&oacute;t b&ecirc;n trong được th&ecirc;u h&agrave;ng vạn hạt kim sa tạo n&ecirc;n những hoa văn ch&igrave;m lung linh ẩn hiện ảo diệu. Tất cả tạo n&ecirc;n một t&aacute;c phẩm tinh tế đầy cảm x&uacute;c như hoa tuyết bay lung linh dưới nắng.</p>\r\n<p style=\"text-align: justify;\">Điều đặc biệt hơn, một mẫu v&aacute;y c&oacute; thể &ldquo;biến h&igrave;nh&rdquo; một c&aacute;ch&nbsp;kỳ diệu, được thiết kế t&aacute;ch rời bởi 9 phần ri&ecirc;ng biệt, gi&uacute;p chiếc v&aacute;y c&oacute; thể thay đổi từ d&aacute;ng đu&ocirc;i c&aacute; &ocirc;m s&aacute;t, gợi cảm, th&agrave;nh chi&eacute;c v&aacute;y A mềm mại nữ t&iacute;nh, v&agrave; cuối c&ugrave;ng l&agrave; mẫu v&aacute;y ho&agrave;ng gia đu&ocirc;i d&agrave;i lộng lẫy.</p>\r\n<p style=\"text-align: justify;\">Ch&iacute;nh nhờ sự biến ho&aacute; đặc biệt n&agrave;y m&agrave; &Aacute; hậu T&uacute; Anh c&oacute; thể dễ d&agrave;ng ho&aacute; th&acirc;n đầy biến ảo từ một c&ocirc; d&acirc;u gợi cảm th&agrave;nh một nữ ho&agrave;ng hoa tuyết đầy uy quyền chỉ trong v&ograve;ng gi&acirc;y l&aacute;t.</p>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<div style=\"text-align: center;\">\r\n<div><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-19/1513653099-102-tu-anh2-1513649883-width660height897.jpg\" alt=\"&amp;#34;Bạn g&aacute;i Noo Phước Thịnh&amp;#34; mặc v&aacute;y cưới 3 tỷ đồng - 4\" />\r\n<div id=\"24h-banner-in-image\">&nbsp;</div>\r\n</div>\r\n</div>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<p style=\"text-align: center;\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-19/1513653099-7-t---anh-9-1513650404-width660height990.jpg\" alt=\"&amp;#34;Bạn g&aacute;i Noo Phước Thịnh&amp;#34; mặc v&aacute;y cưới 3 tỷ đồng - 6\" /></p>\r\n<p style=\"text-align: center;\">T&uacute; Anh từng nhiều lần l&agrave;m mẫu chụp ảnh v&aacute;y cưới</p>\r\n<p style=\"text-align: justify;\">Sở hữu gương mặt sang trọng đậm chất &Aacute; đ&ocirc;ng, l&agrave;n da trắng sứ c&ugrave;ng đường cong quyến rũ, &Aacute; hậu T&uacute; Anh kh&ocirc;ng mấy kh&oacute; khăn khi&nbsp;tạo h&igrave;nh c&ocirc; d&acirc;u&nbsp;b&iacute; ẩn. Hiện tại ngo&agrave;i vai tr&ograve; l&agrave; bi&ecirc;n tập vi&ecirc;n của đ&agrave;i truyền h&igrave;nh Việt Nam, T&uacute; Anh c&ograve;n đảm nhận vai tr&ograve; gi&aacute;m đốc một trung t&acirc;m thời trang.&nbsp;Từng v&agrave;i lần l&agrave;m người mẫu mặc v&aacute;y cưới nhưng khi được hỏi l&uacute;c n&agrave;o l&ecirc;n xe hoa, T&uacute; Anh chỉ cười trừ. C&ocirc; khẳng định m&igrave;nh kh&ocirc;ng phải l&agrave; tu&yacute;p người n&ocirc;n n&oacute;ng l&agrave;m đ&aacute;m cưới khi ở độ tuổi c&ograve;n qu&aacute; trẻ m&agrave; vẫn phấn đấu cho sự nghiệp để c&oacute; một chỗ đứng ri&ecirc;ng.</p>\r\n<p style=\"text-align: justify;\">&Aacute; hậu&nbsp;bật m&iacute; mẹ của c&ocirc; đ&atilde; kh&aacute; sốt ruột v&igrave; con g&aacute;i đăng quang &aacute;&nbsp;hậu đ&atilde; 5 năm m&agrave; vẫn chưa c&oacute; &yacute; định lập gia thất, trong khi đ&oacute; người c&ugrave;ng đăng quang năm đ&oacute; l&agrave; Hoa hậu Thu Thảo th&igrave; đ&atilde; lấy chồng.&nbsp;<em>\"Tuy sốt ruột l&agrave; vậy nhưng mẹ T&uacute; Anh lại rất hiểu v&agrave; t&acirc;m l&yacute; với con g&aacute;i n&ecirc;n kh&ocirc;ng c&agrave;u nh&agrave;u g&igrave; về chuyện lấy chồng cả\"&nbsp;</em>&ndash; T&uacute; Anh t&acirc;m sự.</p>\r\n<p style=\"text-align: center;\" align=\"center\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-19/1513653099-693-tu-anh-1513649188-width660height766.jpg\" alt=\"&amp;#34;Bạn g&aacute;i Noo Phước Thịnh&amp;#34; mặc v&aacute;y cưới 3 tỷ đồng - 7\" /></p>\r\n<p style=\"text-align: center;\" align=\"center\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-19/1513653099-835-t---anh7-1513650508-width660height885.jpg\" alt=\"&amp;#34;Bạn g&aacute;i Noo Phước Thịnh&amp;#34; mặc v&aacute;y cưới 3 tỷ đồng - 8\" /></p>\r\n<p style=\"text-align: center;\" align=\"center\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-19/1513653099-111-t---anh8-1513650515-width660height636.jpg\" alt=\"&amp;#34;Bạn g&aacute;i Noo Phước Thịnh&amp;#34; mặc v&aacute;y cưới 3 tỷ đồng - 9\" /></p>\r\n<p style=\"text-align: center;\" align=\"center\">Chiếc v&aacute;y cưới c&oacute; thể biến h&oacute;a th&agrave;nh nhiều kiểu kh&aacute;c nhau</p>\r\n<p style=\"text-align: center;\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-19/1513653100-324-t---anh3-1513650126-width660height937.jpg\" alt=\"&amp;#34;Bạn g&aacute;i Noo Phước Thịnh&amp;#34; mặc v&aacute;y cưới 3 tỷ đồng - 10\" /></p>\r\n<p style=\"text-align: center;\">Th&ecirc;m kiểu v&aacute;y đơn giản hơn nhưng đầy quyến rũ của T&uacute; Anh</p>', 'thoitrang2.jpg', '20-05-2024', '15:49:17', 1, 1, 1, 0),
(22, 'Mang bầu kiểu mỹ nhân: Đẹp rạng ngời dù bụng vượt mặt', 'Công nương Kate thậm chí còn xinh đẹp, hồng', '<p align=\"center\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-19/1513616851-953-mang-bau-kieu-my-nhan-dep-rang-ngoi-du-bung-vuot-mat-0-1513586104-width500height833.jpg\" alt=\"Mang bầu kiểu mỹ nh&acirc;n: Đẹp rạng ngời d&ugrave; bụng vượt mặt - 1\" /></p>\r\n<p>Ai mang bầu cũng sợ xấu nhưng c&aacute;c mỹ nh&acirc;n nổi tiếng th&igrave; kh&ocirc;ng</p>\r\n<p>Mang bầu được coi l&agrave; một trong những hỉ sự vĩ đại nhất trong cuộc đời nhiều phụ nữ. Song, đ&oacute; l&agrave; về mặt tinh thần. C&ograve;n về mặt h&igrave;nh thức, kh&ocirc;ng &iacute;t chị em lo sợ rằng bầu b&igrave; sẽ khiến m&igrave;nh tăng c&acirc;n mất kiểm so&aacute;t, nội tiết thay đổi khiến da dẻ xuống cấp v&agrave; rất nhiều cơn &aacute;c mộng kh&aacute;c mang t&ecirc;n ngoại h&igrave;nh cứ tiếp diễn nhau cho tới khi sinh nở, thậm ch&iacute; cả sau khi đ&atilde; vượt cạn rất l&acirc;u.</p>\r\n<p>Nhưng đ&oacute; l&agrave; với người thường, c&ograve;n với c&aacute;c mỹ nh&acirc;n tr&ecirc;n thế giới - những người nổi tiếng th&igrave; sao? May mắn sở hữu vẻ bề ngo&agrave;i nổi trội đ&atilde; đ&agrave;nh, họ lại c&oacute; đủ thứ c&ocirc;ng cụ để chăm s&oacute;c bản th&acirc;n l&uacute;c mang bầu, vậy n&ecirc;n ai nấy đều xinh đẹp kh&ocirc;ng kh&aacute;c mấy khi son rỗi, ngoại trừ chiếc bụng nh&ocirc; cao.</p>\r\n<p>Dưới đ&acirc;y l&agrave; những mỹ nh&acirc;n đ&oacute;n tin vui trong năm 2017 vừa qua v&agrave; vẫn chứng tỏ đẳng cấp nhan sắc d&ugrave; ở trong giai đoạn kh&oacute; kiểm so&aacute;t nhất:</p>\r\n<p align=\"center\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-19/1513616851-219-mang-bau-kieu-my-nhan-dep-rang-ngoi-du-bung-vuot-mat-1-1513586104-width500height500.jpg\" alt=\"Mang bầu kiểu mỹ nh&acirc;n: Đẹp rạng ngời d&ugrave; bụng vượt mặt - 2\" /></p>\r\n<p>Sở hữu v&oacute;c d&aacute;ng mảnh mai, thậm ch&iacute; c&oacute; phần &ldquo;c&ograve; hương&rdquo; n&ecirc;n d&ugrave; đang mang bầu tập 3, tr&ocirc;ng c&ocirc;ng nương Kate vẫn kh&ocirc;ng bị ph&aacute;t tướng l&agrave; mấy. C&ocirc; thậm ch&iacute; c&ograve;n xinh đẹp, hồng h&agrave;o hơn xưa v&igrave; đ&atilde; tăng c&acirc;n được ch&uacute;t &iacute;t kể từ khi điện Kensington th&ocirc;ng b&aacute;o Kate sắp cho ho&agrave;ng tử George v&agrave; Charlotte c&oacute; th&ecirc;m em.</p>\r\n<p align=\"center\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-19/1513616852-67-mang-bau-kieu-my-nhan-dep-rang-ngoi-du-bung-vuot-mat-2-1513586104-width500height727.jpg\" alt=\"Mang bầu kiểu mỹ nh&acirc;n: Đẹp rạng ngời d&ugrave; bụng vượt mặt - 3\" /></p>\r\n<p>Bụng đ&atilde; nh&ocirc; cao thấy r&otilde;, ch&acirc;n tay cũng c&oacute; phần mũm mĩm hơn, ấy thế m&agrave; vẻ rạng rỡ của b&agrave; mẹ sắp 3 con Jessica Alba vẫn cứ như th&aacute;ch thức cả thế giới! Đ&aacute;ng n&oacute;i l&agrave; năm nay, c&ocirc; cũng đ&atilde; 36 tuổi, kh&ocirc;ng c&ograve;n qu&aacute; trẻ. Mang bầu dường như chỉ khiến &ldquo;thi&ecirc;n thần b&oacute;ng tối&rdquo; th&ecirc;m xinh đẹp, mặn m&agrave;.</p>\r\n<p>&nbsp;</p>\r\n<div class=\"banner_inread\">&nbsp;</div>\r\n<p>&nbsp;</p>\r\n<p align=\"center\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-19/1513616852-179-mang-bau-kieu-my-nhan-dep-rang-ngoi-du-bung-vuot-mat-3-1513586104-width500height733.jpg\" alt=\"Mang bầu kiểu mỹ nh&acirc;n: Đẹp rạng ngời d&ugrave; bụng vượt mặt - 4\" /></p>\r\n<p>Ngo&agrave;i phần bụng tr&ograve;n l&ecirc;n đ&ocirc;i ch&uacute;t, chẳng ai nhận ra Miranda Kerr đang mang bầu cho tới khi c&ocirc; v&agrave; tỷ ph&uacute; Evan Spiegel th&ocirc;ng b&aacute;o họ đang đ&oacute;n chờ th&agrave;nh vi&ecirc;n mới trong gia đ&igrave;nh. Dự kiến năm 2018 tới đ&acirc;y, Miranda Kerr sẽ trở th&agrave;nh một trong những &ldquo;b&agrave; mẹ bỉm sữa&rdquo; hot nhất h&agrave;nh tinh!</p>\r\n<p align=\"center\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-19/1513616852-232-mang-bau-kieu-my-nhan-dep-rang-ngoi-du-bung-vuot-mat-4-1513586104-width500height647.jpg\" alt=\"Mang bầu kiểu mỹ nh&acirc;n: Đẹp rạng ngời d&ugrave; bụng vượt mặt - 5\" /></p>\r\n<p>B&agrave; x&atilde; của nam ca sĩ Adam Levine - đồng thời l&agrave; si&ecirc;u mẫu Behati Prinsloo cũng đang chờ mong ng&agrave;y ch&agrave;o đời của đứa con thứ hai. Ch&acirc;n d&agrave;i người Nam Phi tr&ograve;n trịa l&ecirc;n tr&ocirc;ng thấy nhưng ai cũng c&ocirc;ng nhận c&ocirc; quyến rũ hơn nhiều v&igrave; trước kia gầy g&ograve; qu&aacute;.</p>\r\n<p align=\"center\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-19/1513616852-444-mang-bau-kieu-my-nhan-dep-rang-ngoi-du-bung-vuot-mat-5-1513586104-width500height750.jpg\" alt=\"Mang bầu kiểu mỹ nh&acirc;n: Đẹp rạng ngời d&ugrave; bụng vượt mặt - 6\" /></p>\r\n<p>Mọi b&agrave; mẹ bỉm sữa c&oacute; lẽ đều ghen tị với sắc v&oacute;c c&ugrave;ng phong c&aacute;ch thời trang s&agrave;nh điệu của Chrissy Teigen. Cũng phải th&ocirc;i, v&igrave; c&ocirc; l&agrave; người mẫu nổi tiếng, lại l&agrave; vợ của John Legend - nam ca sĩ từng gi&agrave;nh Grammy cơ m&agrave;! Đang mang bầu b&eacute; thứ 2, Chrissy Teigen kh&ocirc;ng hề lộ vẻ mệt mỏi hay k&eacute;m sắc g&igrave;, ngược lại, c&ocirc; vẫn cứ xinh đẹp v&agrave; ăn mặc gợi cảm khi ra phố.</p>\r\n<p align=\"center\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-19/1513616852-388-mang-bau-kieu-my-nhan-dep-rang-ngoi-du-bung-vuot-mat-6-1513586104-width500height505.jpg\" alt=\"Mang bầu kiểu mỹ nh&acirc;n: Đẹp rạng ngời d&ugrave; bụng vượt mặt - 7\" /></p>\r\n<p>Thi&ecirc;n thần Victoria&rsquo;s Secret Candice Swanepoel vừa th&ocirc;ng b&aacute;o đang mang bầu lần hai với vị h&ocirc;n phu Hermann Nicoli. V&agrave; ai m&agrave; c&oacute; thể nhận ra ch&acirc;n d&agrave;i Nam Phi đ&atilde; c&oacute; tin vui từ l&uacute;c c&ograve;n tất bật chuẩn bị cho show Victoria&rsquo;s Secret c&aacute;ch đ&acirc;y 1 th&aacute;ng? Chỉ bởi c&ocirc; l&uacute;c n&agrave;o cũng thon gọn v&agrave; duy&ecirc;n d&aacute;ng.</p>\r\n<p align=\"center\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-19/1513616852-699-mang-bau-kieu-my-nhan-dep-rang-ngoi-du-bung-vuot-mat-7-1513586104-width500height499.jpg\" alt=\"Mang bầu kiểu mỹ nh&acirc;n: Đẹp rạng ngời d&ugrave; bụng vượt mặt - 8\" /></p>\r\n<p>Nếu mang bầu m&agrave; chẳng kh&aacute;c g&igrave; ngo&agrave;i chiếc bụng như c&ocirc; mẫu April Love Geary - bạn g&aacute;i của nam ca sĩ Robin Thicke th&igrave; chắc phụ nữ cả thế giới kh&ocirc;ng ai c&ograve;n sợ bầu b&igrave; nữa.</p>\r\n<p align=\"center\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-19/1513616852-557-mang-bau-kieu-my-nhan-dep-rang-ngoi-du-bung-vuot-mat-8-1513586104-width500height756.jpg\" alt=\"Mang bầu kiểu mỹ nh&acirc;n: Đẹp rạng ngời d&ugrave; bụng vượt mặt - 9\" /></p>\r\n<p>Bikini c&oacute; thể l&agrave; nỗi e ngại của qu&yacute; b&agrave;, qu&yacute; c&ocirc; n&agrave;o đấy nhưng với Ferne McCann - người mẫu 27 tuổi đến từ Anh th&igrave; kh&ocirc;ng phải vấn đề. Từ khi mang bầu, ch&acirc;n d&agrave;i 1m73 thậm ch&iacute; c&ograve;n chăm mặc đồ gợi cảm hơn v&igrave; c&ocirc; thực sự hạnh ph&uacute;c v&agrave; tự h&agrave;o.</p>\r\n<p align=\"center\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-19/1513616853-553-mang-bau-kieu-my-nhan-dep-rang-ngoi-du-bung-vuot-mat-9-1513586104-width500height706.jpg\" alt=\"Mang bầu kiểu mỹ nh&acirc;n: Đẹp rạng ngời d&ugrave; bụng vượt mặt - 10\" /></p>\r\n<p>Ở tuổi 35, nữ diễn vi&ecirc;n Kirsten Dunst tr&ocirc;ng c&agrave;ng ngọt ng&agrave;o hơn khi đang mang bầu đứa con đầu l&ograve;ng với h&ocirc;n phu Jesse Plemons.</p>\r\n<p align=\"center\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-19/1513616853-899-mang-bau-kieu-my-nhan-dep-rang-ngoi-du-bung-vuot-mat-10-1513586104-width500height750.jpg\" alt=\"Mang bầu kiểu mỹ nh&acirc;n: Đẹp rạng ngời d&ugrave; bụng vượt mặt - 11\" /></p>\r\n<p>Fan h&acirc;m mộ của nữ ca sĩ Lauren Hashian th&iacute;ch nh&igrave;n thần tượng của m&igrave;nh khi mang bầu hơn v&igrave; b&igrave;nh thường, c&ocirc; tr&ocirc;ng kh&aacute; gầy. Đ&acirc;y l&agrave; đứa con thứ hai của c&ocirc; với chồng l&agrave; cựu đ&ocirc; vật ki&ecirc;m diễn vi&ecirc;n Dwayne Johnson.</p>', 'thoitrang3.jpg', '20-05-2024', '15:52:26', 1, 1, 1, 0),
(23, 'Hà Hồ đẹp tựa nữ thần với váy ren lưới nghìn lỗ', '\"Nữ hoàng giải trí\" khiến khán giả đứng ngồi không yên khi xuất hiện tựa một nữ thần trong những thiết kế mới nhất đến từ bộ sưu tập của NTK Chung Thanh Phong.', '<p><img class=\"news-image\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-18/1513581565-708-haho--8--min-1513571072-width660height990.jpg\" alt=\"H&agrave; Hồ đẹp tựa nữ thần với v&aacute;y ren lưới ngh&igrave;n lỗ - 2\" /></p>\r\n<p style=\"text-align: center;\">Được biết, Hồ Ngọc H&agrave; l&agrave; một trong ba n&agrave;ng thơ ch&iacute;nh của NTK trong dự &aacute;n tạo n&ecirc;n bộ sưu tập đặc biệt t&ocirc;n vinh nhan sắc của ph&aacute;i đẹp.</p>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<div class=\"banner_inread\" style=\"text-align: center;\">&nbsp;</div>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<p style=\"text-align: center;\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-18/1513581565-844-haho--1--min-1513571083-width660height990.jpg\" alt=\"H&agrave; Hồ đẹp tựa nữ thần với v&aacute;y ren lưới ngh&igrave;n lỗ - 3\" /></p>\r\n<p style=\"text-align: center;\">Trong bộ ảnh mới nhất, kh&aacute;n giả c&oacute; cơ hội được nh&igrave;n thấy một Hồ Ngọc H&agrave; lộng lẫy, xa hoa nhưng cũng kh&ocirc;ng k&eacute;m phần quyến rũ, huyền b&iacute;.</p>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<div style=\"text-align: center;\">\r\n<div><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-18/1513581565-838-haho--2--min-1513571094-width660height440.jpg\" alt=\"H&agrave; Hồ đẹp tựa nữ thần với v&aacute;y ren lưới ngh&igrave;n lỗ - 4\" />\r\n<div id=\"24h-banner-in-image\">&nbsp;</div>\r\n</div>\r\n</div>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<p style=\"text-align: center;\">Từ trước đến nay, nữ ca sĩ c&oacute; kh&ocirc;ng &iacute;t bộ h&igrave;nh thời trang đặc sắc, tuy nhi&ecirc;n, Chung Thanh Phong l&agrave; người đầu ti&ecirc;n mang đến h&igrave;nh ảnh một nữ thần Hồ Ngọc H&agrave; thật sự th&ocirc;ng qua những thiết kế thời trang.</p>\r\n<p style=\"text-align: center;\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-18/1513581565-506-haho--4--min-1513571107-width660height990.jpg\" alt=\"H&agrave; Hồ đẹp tựa nữ thần với v&aacute;y ren lưới ngh&igrave;n lỗ - 6\" /></p>\r\n<p style=\"text-align: center;\">H&oacute;a th&acirc;n th&agrave;nh một nữ thần, Hồ Ngọc H&agrave; diện những chiếc đầm cao cấp nhất BST của nh&agrave; thiết kế 8X.</p>\r\n<p style=\"text-align: center;\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-18/1513581565-262-haho--5--min-1513571119-width660height440.jpg\" alt=\"H&agrave; Hồ đẹp tựa nữ thần với v&aacute;y ren lưới ngh&igrave;n lỗ - 7\" /></p>\r\n<p style=\"text-align: center;\">Những thiết kế m&agrave; nữ ca sĩ diện rất đa dạng về kiểu d&aacute;ng, từ đầm đu&ocirc;i c&aacute;, xếp tầng, hở vai,...nhưng đều c&oacute; điểm chung l&agrave; t&ocirc;n d&aacute;ng với những đường cắt t&aacute;o bạo m&agrave; vẫn thể hiện sự tinh tế.</p>\r\n<p style=\"text-align: center;\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-18/1513581565-529-haho--6--min-1513571134-width660height990.jpg\" alt=\"H&agrave; Hồ đẹp tựa nữ thần với v&aacute;y ren lưới ngh&igrave;n lỗ - 8\" /></p>\r\n<p style=\"text-align: center;\">Ngo&agrave;i ra, c&aacute;c chi tiết th&ecirc;u, đ&iacute;nh pha l&ecirc;, đ&iacute;nh hạt, sequin đều được thực hiện ho&agrave;n to&agrave;n bằng tay để tiến đến c&aacute;c ti&ecirc;u chuẩn của đầm haute couture.</p>\r\n<p style=\"text-align: center;\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-18/1513581566-5-haho--9--min-1513571148-width660height990.jpg\" alt=\"H&agrave; Hồ đẹp tựa nữ thần với v&aacute;y ren lưới ngh&igrave;n lỗ - 9\" /></p>\r\n<p style=\"text-align: center;\">Nữ ca sĩ được tạo h&igrave;nh l&ocirc;i cuốn với m&aacute;i t&oacute;c bu&ocirc;ng d&agrave;i bấm nhẹ, sử dụng tone trang điểm trầm để to&aacute;t l&ecirc;n vẻ đẹp quyến rũ, b&iacute; ẩn. &nbsp;</p>\r\n<p style=\"text-align: center;\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-18/1513581566-327-haho--10--min-1513571156-width660height440.jpg\" alt=\"H&agrave; Hồ đẹp tựa nữ thần với v&aacute;y ren lưới ngh&igrave;n lỗ - 10\" /></p>\r\n<p style=\"text-align: center;\">Hồ Ngọc H&agrave; v&agrave; Chung Thanh Phong c&oacute; mối quan hệ th&acirc;n thiết với nhau từ trước. Nữ ca sĩ thường chọn c&aacute;c trang phục của NTK để tham gia c&aacute;c sự kiện quan trọng.</p>\r\n<p style=\"text-align: center;\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-18/1513581566-474-haho--11--min-1513571173-width660height440.jpg\" alt=\"H&agrave; Hồ đẹp tựa nữ thần với v&aacute;y ren lưới ngh&igrave;n lỗ - 11\" /></p>\r\n<p style=\"text-align: center;\">Trong khi đ&oacute;, Chung Thanh Phong rất y&ecirc;u mến Hồ Ngọc H&agrave; v&agrave; từng mời nữ ca sĩ tham gia c&aacute;c dự &aacute;n thời trang của anh, gần đ&acirc;y nhất l&agrave; BST Love Your Body, Be Yourself.</p>\r\n<p style=\"text-align: center;\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-18/1513581566-371-haho--12--min-1513571183-width660height440.jpg\" alt=\"H&agrave; Hồ đẹp tựa nữ thần với v&aacute;y ren lưới ngh&igrave;n lỗ - 12\" /></p>\r\n<p style=\"text-align: center;\">Với She\'s A Goddess, khi bắt tay v&agrave;o ph&aacute;c thảo, Chung Thanh Phong lu&ocirc;n t&igrave;m cảm hứng từ h&igrave;nh ảnh Hồ Ngọc H&agrave; để thổi hồn v&agrave;o c&aacute;c thiết kế.</p>\r\n<p style=\"text-align: center;\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-18/1513581566-259-haho--13--min-1513571192-width660height990.jpg\" alt=\"H&agrave; Hồ đẹp tựa nữ thần với v&aacute;y ren lưới ngh&igrave;n lỗ - 13\" /></p>\r\n<p style=\"text-align: center;\">Từ đ&oacute; gi&uacute;p cho anh c&oacute; được động lực to lớn để tạo n&ecirc;n những thiết kế m&agrave; bất k&igrave; ai diện l&ecirc;n người cũng c&oacute; thể trở th&agrave;nh một nữ thần thật sự.&nbsp;</p>\r\n<p style=\"text-align: center;\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-18/1513581566-250-haho--14--min-1513571205-width660height990.jpg\" alt=\"H&agrave; Hồ đẹp tựa nữ thần với v&aacute;y ren lưới ngh&igrave;n lỗ - 14\" /></p>\r\n<p style=\"text-align: center;\">Mặc d&ugrave; lịch tr&igrave;nh kh&ocirc;ng cho ph&eacute;p Hồ Ngọc H&agrave; tham dự đ&ecirc;m ra mắt BST nhưng c&ocirc; cũng đ&atilde; kịp gửi lời ch&uacute;c mừng đến Chung Thanh Phong. K&egrave;m theo đ&oacute;, c&ocirc; c&ograve;n hứa rằng sẽ lu&ocirc;n ủng hộ c&aacute;c thiết kế n&agrave;y hết m&igrave;nh v&agrave; tiết lộ rằng sẽ mang \"nữ thần\" l&ecirc;n s&acirc;n khấu trong một ng&agrave;y kh&ocirc;ng xa.</p>', 'thoitrang4.jpg', '20-05-2024', '15:58:54', 1, 1, 1, 0),
(24, 'Nhiều cô gái Hà thành diện mốt \"nói không với quần\" giữa đông rét cắt da cắt thịt', 'Phố đi bộ của Hà Nội ngoài là địa điểm vui chơi mỗi dịp cuối tuần thì nó còn là sàn diễn thời trang để giới trẻ thỏa sức thể hiện bản thân.', '<p>Từ l&acirc;u mỗi dịp cuối tuần, phố đi bộ H&agrave; Nội được coi như tụ điểm ăn chơi của người d&acirc;n nơi đ&acirc;y. B&ecirc;n cạnh những hoạt động văn h&oacute;a th&igrave; phố đi bộ c&ograve;n được giới trẻ coi như s&agrave;n diễn thời trang sinh động với rất nhiều phong c&aacute;ch, m&agrave;u sắc kh&aacute;c nhau cực th&uacute; vị. Tuy nhi&ecirc;n, khi trời v&agrave;o đ&ocirc;ng với c&aacute;i lạnh t&ecirc; t&aacute;i với thời tiết xuống gần 10 độ C, c&aacute;c bạn trẻ cũng chẳng hề hấn g&igrave; m&agrave; diện phong c&aacute;ch \"thời trang phang thời tiết\", n&oacute;i kh&ocirc;ng với quần, khoe ch&acirc;n d&agrave;i đầy th&aacute;ch thức.</p>\r\n<p>Ngo&agrave;i những chiếc quần ngắn cũn cỡn, c&aacute;c c&ocirc; n&agrave;ng kh&ocirc;ng ngại diện &aacute;o trễ vai, ch&acirc;n v&aacute;y tennis tung tăng b&ecirc;n hội bạn với phong th&aacute;i đầy tự tin. Khi nhu cầu mặc đẹp l&ecirc;n ng&ocirc;i, c&oacute; lạnh tới mấy cũng kh&ocirc;ng cản ch&acirc;n c&aacute;c bạn trẻ \"bung lụa\" giữa trời đ&ocirc;ng.&nbsp;</p>\r\n<p align=\"center\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-18/1513574898-934-gioi-tre-ha-thanh-noi-khong-voi-quan-xuong-pho-giua-dong-ret-a--8--1513568915-width600height1000.jpg\" alt=\"Nhiều c&ocirc; g&aacute;i H&agrave; th&agrave;nh diện mốt &amp;#34;n&oacute;i kh&ocirc;ng với quần&amp;#34; giữa đ&ocirc;ng r&eacute;t cắt da cắt thịt - 2\" /></p>\r\n<p>D&ugrave; c&oacute; lạnh tới mấy, kho&aacute;c &aacute;o l&ocirc;ng cũng đủ l&agrave;m ấm cơ thể những ng&agrave;y gi&aacute; r&eacute;t lại sang chảnh kh&ocirc;ng ai bằng</p>\r\n<p align=\"center\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-18/1513574898-596-gioi-tre-ha-thanh-noi-khong-voi-quan-xuong-pho-giua-dong-ret-a--6--1513568915-width600height900.jpg\" alt=\"Nhiều c&ocirc; g&aacute;i H&agrave; th&agrave;nh diện mốt &amp;#34;n&oacute;i kh&ocirc;ng với quần&amp;#34; giữa đ&ocirc;ng r&eacute;t cắt da cắt thịt - 3\" /></p>\r\n<p>Ch&acirc;n v&aacute;y xẻ cao ng&uacute;t, &aacute;o bra, tất lưới... l&agrave; những m&oacute;n đồ thời trang để hội con g&aacute;i sexy giữa m&ugrave;a đ&ocirc;ng</p>\r\n<p>Chị Trần Phương (quận Đống Đa - H&agrave; Nội) ph&aacute;t biển cảm nghĩ khi nh&igrave;n c&aacute;c bạn trẻ ăn mặc phong phanh: \"<em>T&ocirc;i nh&igrave;n thấy c&oacute; ch&uacute;t r&ugrave;ng m&igrave;nh v&igrave; trời qu&aacute; lạnh m&agrave; tại sao c&aacute;c bạn ấy c&oacute; thể mặc như thế, phải chăng c&aacute;c bạn trẻ kh&ocirc;ng sợ&nbsp;thời tiết r&eacute;t mướt?\".&nbsp;</em></p>\r\n<p>Trong khi đ&oacute;, Nguyễn Phương Linh (Đại học Mở H&agrave; Nội) lại tỏ ra ủng hộ với thời trang xua tan thời tiết: \"<em>Em cảm thấy b&igrave;nh thường, chuyện ăn mặc mấy năm gần đ&acirc;y kh&aacute; thoải m&aacute;i, c&aacute;c bạn ấy cảm thấy mặc như thế l&agrave; đủ ấm đủ đẹp, kh&ocirc;ng qu&aacute; phản cảm th&igrave; m&igrave;nh c&oacute; l&yacute; g&igrave; để m&agrave; chỉ ch&iacute;ch\".</em></p>\r\n<p>&nbsp;</p>\r\n<div class=\"banner_inread\">&nbsp;</div>\r\n<p>&nbsp;</p>\r\n<p align=\"center\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-18/1513574898-36-gioi-tre-ha-thanh-noi-khong-voi-quan-xuong-pho-giua-dong-ret-a--10--1513568915-width600height800.jpg\" alt=\"Nhiều c&ocirc; g&aacute;i H&agrave; th&agrave;nh diện mốt &amp;#34;n&oacute;i kh&ocirc;ng với quần&amp;#34; giữa đ&ocirc;ng r&eacute;t cắt da cắt thịt - 4\" /></p>\r\n<p>Diện &aacute;o len trễ vai, c&ocirc; g&aacute;i vẫn đảm bảo ti&ecirc;u ch&iacute; ấm - đẹp - sexy</p>\r\n<p align=\"center\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-18/1513574898-664-gioi-tre-ha-thanh-noi-khong-voi-quan-xuong-pho-giua-dong-ret-a--11--1513568915-width600height600.jpg\" alt=\"Nhiều c&ocirc; g&aacute;i H&agrave; th&agrave;nh diện mốt &amp;#34;n&oacute;i kh&ocirc;ng với quần&amp;#34; giữa đ&ocirc;ng r&eacute;t cắt da cắt thịt - 5\" /></p>\r\n<p>Ai n&oacute;i trời lạnh th&igrave; kh&ocirc;ng thể mặc \"hở da hở thịt\" được</p>\r\n<p align=\"center\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-18/1513574898-273-gioi-tre-ha-thanh-noi-khong-voi-quan-xuong-pho-giua-dong-ret-a--12--1513568915-width600height800.jpg\" alt=\"Nhiều c&ocirc; g&aacute;i H&agrave; th&agrave;nh diện mốt &amp;#34;n&oacute;i kh&ocirc;ng với quần&amp;#34; giữa đ&ocirc;ng r&eacute;t cắt da cắt thịt - 6\" /></p>\r\n<p>Rất nhiều bạn trẻ hồn nhi&ecirc;n mặc &aacute;o tr&ugrave;m quần xuống phố khi đ&ecirc;m H&agrave; Nội gần 10 độ</p>\r\n<p align=\"center\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-18/1513574898-896-gioi-tre-ha-thanh-noi-khong-voi-quan-xuong-pho-giua-dong-ret-a--13--1513568915-width600height800.jpg\" alt=\"Nhiều c&ocirc; g&aacute;i H&agrave; th&agrave;nh diện mốt &amp;#34;n&oacute;i kh&ocirc;ng với quần&amp;#34; giữa đ&ocirc;ng r&eacute;t cắt da cắt thịt - 7\" /></p>\r\n<p align=\"center\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-18/1513574899-924-gioi-tre-ha-thanh-noi-khong-voi-quan-xuong-pho-giua-dong-ret-a--9--1513568915-width600height800.jpg\" alt=\"Nhiều c&ocirc; g&aacute;i H&agrave; th&agrave;nh diện mốt &amp;#34;n&oacute;i kh&ocirc;ng với quần&amp;#34; giữa đ&ocirc;ng r&eacute;t cắt da cắt thịt - 8\" /></p>\r\n<p>Thực chất, th&acirc;n nhiệt lu&ocirc;n sẽ c&oacute; một thời gian ngắn để th&iacute;ch nghi từ những m&ocirc;i trường kh&aacute;c nhau v&agrave; t&ugrave;y v&agrave;o thể trang của từng người. V&igrave; vậy, ti&ecirc;u ch&iacute; đẹp m&agrave; ấm của giới trẻ rất đơn giản, stylist Hữu Anh Zoner chia sẻ tuyệt chi&ecirc;u:&nbsp;<em>\"Mặc layer nhiều lớp giữ ấm th&acirc;n tr&ecirc;n với &aacute;o len, &aacute;o kho&aacute;c hay mũ... th&igrave; bạn c&oacute; để hở đ&ocirc;i ch&acirc;n trần cũng kh&ocirc;ng lạnh. H&atilde;y sử dụng những chiếc quần tất như một th&oacute;i quen cho những c&ocirc; n&agrave;ng th&iacute;ch phong c&aacute;ch oversize (giấu quần, ch&acirc;n v&aacute;y ngắn...) nếu kh&ocirc;ng h&atilde;y thử kết hợp c&ugrave;ng tất thể thao cao tới bắp ch&acirc;n để kết hợp c&ugrave;ng để c&oacute; diện mạo trẻ trung.</em></p>\r\n<p><em>Th&ecirc;m một gợi &yacute; nữa đ&oacute; ch&iacute;nh l&agrave; đừng qu&ecirc;n tận dụng những m&oacute;n đồ thời trang xu hướng như boot tất, mũ nồi để mix c&ugrave;ng thật s&agrave;nh điệu xuống phố. Sau c&ugrave;ng, d&agrave;nh cho những c&ocirc; n&agrave;ng c&oacute; thể trạng kh&ocirc;ng tốt bằng c&aacute;ch mặc &aacute;o l&oacute;t giữ nhiệt, bất chấp thời tiết gi&aacute; r&eacute;t\".</em></p>\r\n<p align=\"center\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-18/1513574899-772-gioi-tre-ha-thanh-noi-khong-voi-quan-xuong-pho-giua-dong-ret-a--9--1513570247-width960height640.jpg\" alt=\"Nhiều c&ocirc; g&aacute;i H&agrave; th&agrave;nh diện mốt &amp;#34;n&oacute;i kh&ocirc;ng với quần&amp;#34; giữa đ&ocirc;ng r&eacute;t cắt da cắt thịt - 9\" /></p>\r\n<p>Stylist Hữu Anh Zoner</p>', 'thoitrang5.jpg', '20-05-2024', '16:00:57', 1, 1, 1, 0),
(25, 'Gái xinh chỉ mặc độc quần áo vẽ ra phố và phản ứng khó tin của mọi người', 'Lạ thay, không một ai phát hiện ra cô nàng đang không mặc gì ngoài quần áo vẽ!', '<p>Lili Jasmin - ở giữa, l&agrave; người thực hiện thử th&aacute;ch mặc quần &aacute;o vẽ ra phố nh&acirc;n dịp gi&aacute;ng sinh sắp tới</p>\r\n<p>Gi&aacute;ng sinh chưa đến nhưng người ta đ&atilde; nghĩ ra đủ chi&ecirc;u tr&ograve; để ăn mừng đảm bảo &ldquo;kh&ocirc;ng đụng h&agrave;ng&rdquo;.</p>\r\n<p>Với c&ocirc; mẫu Lili Jasmin, c&ocirc; chọn c&aacute;ch mặc &ldquo;quần &aacute;o vẽ&rdquo; ra phố, h&ograve;a v&agrave;o d&ograve;ng người đang tận hưởng kh&ocirc;ng kh&iacute; gi&aacute;ng sinh tr&ecirc;n phố, trong c&aacute;c qu&aacute;n bar để theo d&otilde;i phản ứng của họ.</p>\r\n<p>Chỉ mặc độc một chiếc underwear m&agrave;u da, Lili Jasmin để mặc cho c&aacute;c họa sĩ chuy&ecirc;n vẽ body painting mặc nhi&ecirc;n s&aacute;ng tạo tr&ecirc;n cơ thể m&igrave;nh.</p>\r\n<p>V&agrave; kh&ocirc;ng bao l&acirc;u sau, Lili đ&atilde; c&oacute; ngay bộ &ldquo;trang phục&rdquo; gồm &aacute;o len họa tiết c&acirc;y th&ocirc;ng mix hai m&agrave;u đen - trắng, quần legging với h&igrave;nh b&ocirc;ng tuyết quanh h&ocirc;ng v&agrave; sọc xanh đỏ đ&uacute;ng chất gi&aacute;ng sinh. Dĩ nhi&ecirc;n, tất cả đều từ m&agrave;u vẽ m&agrave; ra!</p>\r\n<p align=\"center\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-19/1513658408-203-15-1513652102-width500height374.jpg\" alt=\"G&aacute;i xinh chỉ mặc độc quần &aacute;o vẽ ra phố v&agrave; phản ứng kh&oacute; tin của mọi người - 2\" /></p>\r\n<p align=\"center\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-19/1513658410-266-13-1513651984-width500height361.jpg\" alt=\"G&aacute;i xinh chỉ mặc độc quần &aacute;o vẽ ra phố v&agrave; phản ứng kh&oacute; tin của mọi người - 3\" /></p>\r\n<p>Qu&aacute; tr&igrave;nh h&oacute;a trang cho Lili Jasmin kh&ocirc;ng mất nhiều thời gian nhưng bộ đồ vẫn tr&ocirc;ng rất ho&agrave;n h&agrave;o</p>\r\n<p>C&ocirc; mẫu xinh đẹp tự tin bước ra đường phố New York tấp nập, mặc kệ c&aacute;i lạnh cắt da cắt thịt của những ng&agrave;y cuối đ&ocirc;ng, lao v&agrave;o cả c&aacute;c nh&agrave; h&agrave;ng, qu&aacute;n ăn, qu&aacute;n cafe đ&ocirc;ng như kiến những d&ograve;ng người đang hẹn h&ograve;, tụ tập nh&acirc;n dịp cuối năm để thử xem phản ứng của họ.</p>\r\n<p>Lạ thay, kh&ocirc;ng một ai ph&aacute;t hiện ra c&ocirc; n&agrave;ng đang kh&ocirc;ng mặc g&igrave; ngo&agrave;i quần &aacute;o vẽ!</p>\r\n<p>Khoảnh khắc c&ocirc; n&agrave;ng s&aacute;n lại gần một anh ch&agrave;ng, chỉ cho anh ta thấy thực ra bộ đồ c&ocirc; mặc kh&ocirc;ng phải l&agrave; quần &aacute;o thật, m&agrave; chỉ l&agrave; sản phẩm của m&agrave;u vẽ, anh ch&agrave;ng đ&atilde; v&ocirc; c&ugrave;ng hốt hoảng v&agrave; dường như vẫn kh&ocirc;ng thể tin đ&oacute; l&agrave; sự thật!</p>\r\n<p>&nbsp;</p>\r\n<div class=\"banner_inread\">&nbsp;</div>\r\n<p>&nbsp;</p>\r\n<p>Một anh ch&agrave;ng kh&aacute;c Lili gặp tr&ecirc;n phố thậm ch&iacute; c&ograve;n cởi phanh &aacute;o đ&ograve;i...đổi &aacute;o với c&ocirc; g&aacute;i c&oacute; nụ cười tươi r&oacute;i v&agrave; bộ đồ gi&aacute;ng sinh dễ thương đang đứng trước mặt m&igrave;nh.</p>\r\n<p align=\"center\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-19/1513658410-582-14-1513652044-width500height292.jpg\" alt=\"G&aacute;i xinh chỉ mặc độc quần &aacute;o vẽ ra phố v&agrave; phản ứng kh&oacute; tin của mọi người - 4\" /></p>\r\n<p>Một anh ch&agrave;ng hốt hoảng kh&ocirc;ng tin việc Lili Jasmin đang kh&ocirc;ng-mặc-g&igrave; l&agrave; sự thật</p>\r\n<p align=\"center\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-19/1513658410-480-mau-xinh-nhu-mong-quan-ao-ve-ra-pho-va-phan-ung-kho-tin-cua-moi-nguoi-4-1513651636-width500height277.jpg\" alt=\"G&aacute;i xinh chỉ mặc độc quần &aacute;o vẽ ra phố v&agrave; phản ứng kh&oacute; tin của mọi người - 5\" /></p>\r\n<p>Một anh ch&agrave;ng kh&aacute;c th&igrave; đ&ograve;i đổi &aacute;o với c&ocirc; mẫu bằng được</p>\r\n<p>V&agrave; một nh&oacute;m c&aacute;c qu&yacute; c&ocirc; - vẫn nổi tiếng mang giới t&iacute;nh của những người th&iacute;ch săm soi, sau khi nh&igrave;n chằm chằm v&agrave;o v&ograve;ng 3 của Lili Jasmin tới 20 ph&uacute;t mới d&aacute;m ngập ngừng khẳng định quần &aacute;o của c&ocirc; g&aacute;i ấy chỉ l&agrave; một tr&ograve; đ&ugrave;a.</p>\r\n<p>Nhưng vượt tr&ecirc;n cả mong đợi của Lili Jasmin khi thực hiện thử th&aacute;ch n&agrave;y, đ&oacute; l&agrave; những người c&ocirc; gặp đều v&ocirc; c&ugrave;ng th&acirc;n thiện, bất kể họ c&oacute; tin v&agrave;o những g&igrave; c&ocirc; đang &ldquo;mặc&rdquo; hay kh&ocirc;ng.</p>\r\n<p align=\"center\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-19/1513658410-972-mau-xinh-nhu-mong-quan-ao-ve-ra-pho-va-phan-ung-kho-tin-cua-moi-nguoi-5-1513651636-width500height278.jpg\" alt=\"G&aacute;i xinh chỉ mặc độc quần &aacute;o vẽ ra phố v&agrave; phản ứng kh&oacute; tin của mọi người - 6\" /></p>\r\n<p>&ldquo;N&agrave;y, c&ocirc; ấy c&oacute; phải đang mặc &aacute;o vẽ kh&ocirc;ng nhỉ?&rdquo;</p>\r\n<p>Nh&oacute;m c&aacute;c c&ocirc; g&aacute;i sau c&ugrave;ng sau khi lộ vẻ th&aacute;n phục trước t&agrave;i h&oacute;a trang của Lili đ&atilde; nhanh ch&oacute;ng mời c&ocirc; nhập cuộc vui. Họ c&ugrave;ng nhau nhảy nh&oacute;t theo những điệu nhạc gi&aacute;ng sinh bất hủ, khiến cho người ta tin rằng, d&ugrave; bạn c&oacute; mặc quần &aacute;o thật đẹp, thật ấm, hay dũng cảm kho&aacute;c l&ecirc;n m&igrave;nh bộ đồ vẽ mỏng manh giữa những ng&agrave;y n&agrave;y, th&igrave; gi&aacute;ng sinh vẫn cứ về, v&agrave; vẫn cứ phải vui...</p>\r\n<p align=\"center\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-19/1513658410-945-mau-xinh-nhu-mong-quan-ao-ve-ra-pho-va-phan-ung-kho-tin-cua-moi-nguoi-6-1513651636-width500height280.jpg\" alt=\"G&aacute;i xinh chỉ mặc độc quần &aacute;o vẽ ra phố v&agrave; phản ứng kh&oacute; tin của mọi người - 7\" /></p>\r\n<p align=\"center\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-19/1513658410-139-12-1513651814-width500height314.jpg\" alt=\"G&aacute;i xinh chỉ mặc độc quần &aacute;o vẽ ra phố v&agrave; phản ứng kh&oacute; tin của mọi người - 8\" /></p>\r\n<p>Quần &aacute;o thật hay quần &aacute;o vẽ th&igrave; gi&aacute;ng sinh vẫn cứ phải &ldquo;quẩy&rdquo; c&aacute;i đ&atilde;</p>', 'thoitrang6.jpg', '20-05-2024', '16:09:35', 1, 1, 1, 1);
INSERT INTO `articles` (`ArticleID`, `Title`, `Description`, `Content`, `Img`, `Day`, `Time`, `CategoryID`, `UserID`, `Status`, `Popular`) VALUES
(26, 'Cuộc sống giàu sang, nổi tiếng trước khi tự tử của Jong Hyun (SHINee)', 'Jong Hyun là thành viên nhóm nhạc SHINee. Cuộc sống nổi tiếng của anh trước khi qua đời vốn là niềm ước ao của biết bao người.', '<p>Ở tuổi 27, Jong Hyun c&oacute; những thứ m&agrave; mọi người xung quanh mong muốn. Đ&oacute; l&agrave; một gương mặt nổi bật, vai tr&ograve; quan trọng đến mức kh&oacute; c&oacute; thể thay thế trong nh&oacute;m nhạc nổi tiếng SHINee, một sự nghiệp vững chắc với thu nhập lớn từ tiền bản quyền những ca kh&uacute;c do anh s&aacute;ng t&aacute;c c&ugrave;ng nhiều nguồn thu kh&aacute;c.</p>\r\n<p>Thế nhưng, khi anh nằm xuống, mọi thứ chỉ c&ograve;n l&agrave; h&agrave;o quang qu&aacute; khứ. C&ocirc;ng ch&uacute;ng kh&ocirc;ng khỏi x&oacute;t xa, tiếc nuối khi một t&agrave;i năng đ&atilde; ra đi, để lại đằng sau niềm đam m&ecirc; &acirc;m nhạc c&ograve;n dang dở.</p>\r\n<table class=\"picture\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td class=\"pic\">\r\n<div class=\"photoset-item\"><img src=\"https://znews-photo-td.zadn.vn/w660/Uploaded/qfssu/2017_12_19/kim1.jpg\" alt=\"Cuoc song giau sang, noi tieng truoc khi tu tu cua Jong Hyun (SHINee) hinh anh 1\" width=\"660\" height=\"330\" /></div>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class=\"pCaption caption\">Jong Hyun tự s&aacute;t ở tuổi 27.&nbsp;</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p>Nhưng với Jong Hyun, đ&oacute; c&oacute; lẽ l&agrave; c&aacute;i kết anh đ&atilde; chọn để chấm dứt cuộc sống bế tắc, m&agrave; như anh mi&ecirc;u tả trong bức thư tuyệt mệnh gửi chị g&aacute;i rằng &ldquo;em đ&atilde; khổ sở đến tận b&acirc;y giờ, h&atilde;y để em đi&rdquo;.</p>\r\n<h3>Nhanh ch&oacute;ng gặt h&aacute;i th&agrave;nh c&ocirc;ng c&ugrave;ng SHINee</h3>\r\n<p>Jong Hyun bắt đầu ước mơ &acirc;m nhạc của m&igrave;nh bằng 3 năm luyện tập tại c&ocirc;ng ty giải tr&iacute; h&agrave;ng đầu H&agrave;n Quốc SM Entertainment. Như bao thực tập sinh kh&aacute;c tại m&ocirc;i trường giải tr&iacute; khắc nghiệt Kbiz, Jong Hyun phải vượt qua qu&atilde;ng thời gian luyện tập gian khổ với v&ocirc; v&agrave;n những b&agrave;i luyện tập, kiểm tra khắc nghiệt.</p>\r\n<p>Kết th&uacute;c 3 năm nỗ lực với bao mồ h&ocirc;i v&agrave; cả những giọt nước mắt l&agrave; l&uacute;c Jong Hyun bắt đầu nhận được th&agrave;nh quả xứng đ&aacute;ng. Anh ra mắt với vai tr&ograve; h&aacute;t ch&iacute;nh trong nh&oacute;m nhạc SHINee v&agrave;o năm 2008.</p>\r\n<p>Được th&agrave;nh lập bởi \"&ocirc;ng lớn\" SM, SHINee nhanh ch&oacute;ng nhận được sự ch&uacute; &yacute; v&agrave; y&ecirc;u mến từ lượng fan đ&ocirc;ng đảo, d&ugrave; thực tế họ l&agrave; một trong những nh&oacute;m nhạc bị c&ocirc;ng ty bỏ b&ecirc; nhất.</p>\r\n<p>SHINee được giới thiệu như một nh&oacute;m nhạc theo đuổi &acirc;m nhạc R&amp;B với mục ti&ecirc;u trở th&agrave;nh người ti&ecirc;n phong trong lĩnh vực &acirc;m nhạc, thời trang v&agrave; tr&igrave;nh diễn&hellip; Ngay từ ca kh&uacute;c đầu tay&nbsp;<em>Replay</em>&nbsp;được ph&aacute;t h&agrave;nh v&agrave;o th&aacute;ng 5/2008, nh&oacute;m đ&atilde; tạo n&ecirc;n tr&agrave;o lưu trong cộng đồng mạng với thời trang bắt mắt.</p>\r\n<p>Kh&ocirc;ng l&acirc;u sau th&agrave;nh c&ocirc;ng bước đầu, nh&oacute;m ph&aacute;t h&agrave;nh album&nbsp;<em>The Shinee World&nbsp;</em>c&ugrave;ng nhiều ca kh&uacute;c gi&uacute;p củng cố chỗ đứng tại nền &acirc;m nhạc H&agrave;n Quốc như&nbsp;<em>Ring Ding Dong</em>&nbsp;v&agrave;&nbsp;<em>Lucifer</em>. Trong đ&oacute;,&nbsp;<em>Ring Ding Dong</em>&nbsp;đứng đầu c&aacute;c bảng xếp hạng &acirc;m nhạc H&agrave;n Quốc v&agrave; trở n&ecirc;n phổ biến khắp Ch&acirc;u &Aacute;. &nbsp;</p>\r\n<table class=\"picture\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td class=\"pic\">\r\n<div class=\"photoset-item\"><img title=\"Cuộc sống gi&agrave;u sang, nổi tiếng trước khi tự tử của Jong Hyun (SHINee) h&igrave;nh ảnh 2\" src=\"https://znews-photo-td.zadn.vn/w660/Uploaded/qfssu/2017_12_19/p19meo9kui1tlv1fjalus77b1hp52.jpg\" alt=\"Cuoc song giau sang, noi tieng truoc khi tu tu cua Jong Hyun (SHINee) hinh anh 2\" width=\"800\" height=\"450\" /></div>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class=\"pCaption caption\">SHINee l&agrave; một trong những nh&oacute;m nhạc nổi tiếng nhất H&agrave;n Quốc.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p>Kể từ đ&oacute; đến nay, SHINee bền bỉ hoạt động. Mỗi bước đi của nh&oacute;m đều cho thấy sự trưởng th&agrave;nh, chỉn chu trong &acirc;m nhạc. SHINee c&oacute; thể định h&igrave;nh phong c&aacute;ch &acirc;m nhạc R&amp;B nhưng thực tế họ trong mắt c&ocirc;ng ch&uacute;ng l&agrave; những người lu&ocirc;n s&aacute;ng t&aacute;c, thử sức với nhiều thể loại kh&aacute;c nhau, chẳng hạn funk rock, hip hop hay EDM...</p>\r\n<p>Điều quan trọng, 9 năm hoạt động đ&atilde; đủ để SHINee khẳng định m&igrave;nh l&agrave; một trong những nh&oacute;m nhạc nổi bật nhất khi nhắc tới sự đồng đều về t&agrave;i năng thanh nhạc ở Kpop. Những m&agrave;n vũ đạo phức tạp kh&ocirc;ng chỉ gi&uacute;p c&aacute;c ch&agrave;ng trai 3 lần li&ecirc;n tiếp chiến thắng hạng mục M&agrave;n vũ đạo xuất sắc nhất tại Mnet Asian Music Awards m&agrave; c&ograve;n ghi dấu trong l&ograve;ng c&ocirc;ng ch&uacute;ng y&ecirc;u nhạc.</p>\r\n<p>Đương nhi&ecirc;n, đi c&ugrave;ng sự nổi tiếng c&ograve;n l&agrave; doanh thu khủng SHINee đem về từ h&agrave;ng trăm ngh&igrave;n album b&aacute;n ra c&ugrave;ng thu nhập \"b&eacute;o bở\" từ v&ocirc; số hợp đồng quảng c&aacute;o. Đầu năm 2017, theo một b&agrave;i viết được chia sẻ tr&ecirc;n c&aacute;c trang mạng H&agrave;n Quốc, SHINee thu về 10 tỷ won từ một hợp đồng quảng c&aacute;o.</p>\r\n<h3>Gia t&agrave;i ca kh&uacute;c tự s&aacute;ng t&aacute;c đồ sộ</h3>\r\n<p>T&agrave;i hoa của Jong Hyun kh&ocirc;ng chỉ ở giọng h&aacute;t m&agrave; cả khả năng s&aacute;ng t&aacute;c &acirc;m nhạc cũng được c&ocirc;ng ch&uacute;ng, giới chuy&ecirc;n m&ocirc;n ghi nhận. Anh ch&iacute;nh thức bắt đầu sự nghiệp s&aacute;ng t&aacute;c bằng việc viết lời cho ca kh&uacute;c chủ đề&nbsp;<em>Juliette</em>, tr&iacute;ch từ mini album Romeo ph&aacute;t h&agrave;nh th&aacute;ng 5/2009. Đ&acirc;y l&agrave; một trong những ca kh&uacute;c th&agrave;nh c&ocirc;ng nhất v&agrave; l&agrave;m n&ecirc;n t&ecirc;n tuổi của SHINee.</p>\r\n<p>C&ocirc;ng việc s&aacute;ng t&aacute;c của Jong Hyun sau đ&oacute; ng&agrave;y c&agrave;ng thăng hoa, trong đ&oacute;, ca kh&uacute;c&nbsp;<em>Lucifer</em>&nbsp;cũng đứng đầu nhiều bảng xếp hạng &acirc;m nhạc. Đến nay, giọng h&aacute;t ch&iacute;nh nh&oacute;m SHINee đ&atilde; c&oacute; tr&ecirc;n 65 ca kh&uacute;c do anh tham gia s&aacute;ng t&aacute;c, viết lời.&nbsp;</p>\r\n<p>Trong đ&oacute; c&oacute; nhiều ca kh&uacute;c được thể hiện bởi ch&iacute;nh c&aacute;c th&agrave;nh vi&ecirc;n SHINee hay nh&oacute;m nhạc EXO, Lee Hi&hellip; Bản th&acirc;n anh qua những đợt quảng b&aacute; solo cũng truyền tải &acirc;m nhạc v&agrave; giọng h&aacute;t của m&igrave;nh tới kh&aacute;n giả.</p>\r\n<p>Từ album solo đầu tay&nbsp;<em>Base</em>&nbsp;ra mắt v&agrave;o năm 2015 đến sản phẩm gần nhất l&agrave;&nbsp;<em>The Collection: Story Op.2</em>&nbsp;(th&aacute;ng 4/2017), Jong Hyun đều cho thấy c&aacute; t&iacute;nh ri&ecirc;ng trong c&aacute;c s&aacute;ng t&aacute;c.</p>\r\n<p>Ngo&agrave;i những ca kh&uacute;c mới,&nbsp;<em>Story Op.2&nbsp;</em>gồm 5 b&agrave;i h&aacute;t đ&atilde; được ph&aacute;t h&agrave;nh v&agrave;o năm 2016 th&ocirc;ng qua chương tr&igrave;nh&nbsp;<em>Blue Night Radio</em>&nbsp;do Jong Hyun dẫn dắt. Tất cả ca kh&uacute;c trong album đều do anh tự s&aacute;ng t&aacute;c v&agrave; viết lời.</p>\r\n<table class=\"picture\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td class=\"pic\">\r\n<div class=\"photoset-item\"><img src=\"https://znews-photo-td.zadn.vn/w660/Uploaded/qfssu/2017_12_19/jonghyun01e14925238c1e.jpg\" alt=\"Cuoc song giau sang, noi tieng truoc khi tu tu cua Jong Hyun (SHINee) hinh anh 3\" width=\"600\" height=\"413\" /></div>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class=\"pCaption caption\">Jong Hyun lu&ocirc;n nằm trong danh s&aacute;ch thần tượng c&oacute; thu nhập lớn nhất từ tiền bản quyền.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p>Sở hữu gia t&agrave;i s&aacute;ng t&aacute;c đồ sộ, Jong Hyun lu&ocirc;n nằm trong danh s&aacute;ch những ca sĩ c&oacute; thu nhập lớn nhất nhờ tiền bản quyền ca kh&uacute;c. B&ecirc;n cạnh đ&oacute;, việc l&agrave; một th&agrave;nh vi&ecirc;n c&oacute; nhiều hoạt động solo trong nh&oacute;m nhạc nổi tiếng SHINee cũng đem về cho anh khoản thu nhập lớn.</p>\r\n<p>Bởi thế, nếu kh&ocirc;ng c&oacute; c&acirc;u chuyện trầm cảm dẫn đến tự tử, cuộc sống của Jong Hyun vẫn l&agrave; giấc mộng m&agrave;u hồng với biết bao người đang khao kh&aacute;t được gi&agrave;u c&oacute;, nổi tiếng như anh.</p>\r\n<p>\"T&ocirc;i nghĩ mọi người đều c&oacute; những giấc mơ kh&aacute;c nhau, nhưng t&ocirc;i tin rằng c&aacute;c th&agrave;nh vi&ecirc;n SHINee sẽ lu&ocirc;n tiếp tục phấn đấu v&agrave; l&agrave;m việc cẩn thận, tỉ mỉ hơn. C&aacute; nh&acirc;n t&ocirc;i kh&ocirc;ng c&oacute; tham vọng khi n&oacute;i về th&agrave;nh c&ocirc;ng, nhưng t&ocirc;i muốn đạt được những điều mới mẻ c&ugrave;ng những người m&agrave; t&ocirc;i c&oacute; cơ hội c&ugrave;ng l&agrave;m việc trong suốt qu&atilde;ng đường sự nghiệp của m&igrave;nh\", anh từng n&oacute;i.</p>\r\n<p>&Acirc;m nhạc của anh lu&ocirc;n tr&agrave;n đầy sự s&aacute;ng taọ, nhiệt huyết, v&agrave; bởi thế Jong Hyun đ&atilde; ra đi khi t&igrave;nh y&ecirc;u &acirc;m nhạc c&ograve;n dang dở nhưng ước mơ của anh đ&atilde; được thực hiện bằng ch&iacute;nh những nỗ lực, kh&ocirc;ng ngừng t&igrave;m t&ograve;i, học hỏi.</p>', 'amnhac5.jpg', '19-12-2017', '16:20:15', 2, 1, 1, 1),
(27, 'Chi Dân: \'Tôi không quan tâm Ngọc Anh nói gì\'', 'Trong cuộc họp báo giới thiệu MV mới, Chi Dân khẳng định anh không quan tâm đến những phát ngôn gây sốc của nữ ca sĩ Ngọc Anh.', '<p>Chiều 18/12, ca sĩ&nbsp;<a class=\"topic person autolink\" title=\"Tin tức Chi D&acirc;n\" href=\"https://news.zing.vn/chi-dan-nhan-vat-tieu-diem.html\">Chi D&acirc;n</a>&nbsp;tổ chức họp b&aacute;o giới thiệu MV&nbsp;<em>Chuyện anh vẫn chưa kể</em>&nbsp;tại TP.HCM. B&agrave;i h&aacute;t thuộc thể loại Pop do nhạc sĩ Nguyễn Đ&igrave;nh Vũ s&aacute;ng t&aacute;c.</p>\r\n<p>Trong sự kiện giọng ca&nbsp;<em>Điều anh biết</em>&nbsp;nhận được nhiều c&acirc;u hỏi xung quanh ph&aacute;t ng&ocirc;n ồn &agrave;o của ca sĩ&nbsp;<a class=\"topic person autolink\" title=\"Tin tức Ngọc Anh\" href=\"https://news.zing.vn/ngoc-anh-ca-si-tieu-diem.html\">Ngọc Anh</a>: \"Nhạc của Chi D&acirc;n v&agrave; Only C cũng nổi tiếng th&igrave; nhạc Việt như c&aacute;i chợ\".</p>\r\n<p>Nam ca sĩ phản ứng: \"T&ocirc;i kh&ocirc;ng biết ca sĩ Ngọc Anh l&agrave; ai. T&ocirc;i cũng kh&ocirc;ng quan t&acirc;m hay phản ứng g&igrave; với những ph&aacute;t ng&ocirc;n đ&oacute;. Ph&aacute;t ng&ocirc;n đ&oacute; đ&uacute;ng sai thế n&agrave;o kh&aacute;n giả l&agrave; người ph&aacute;n x&eacute;t tốt nhất. T&ocirc;i kh&ocirc;ng muốn mọi thứ ồn &agrave;o hơn\".</p>\r\n<p>Chi D&acirc;n m&ocirc; tả nhạc của anh l&agrave; thị trường, chứ kh&ocirc;ng phải nhạc chợ. Theo anh nhạc thị trường l&agrave; ph&ugrave; hợp với sở th&iacute;ch của kh&aacute;n giả trẻ. \"Mỗi d&ograve;ng nhạc đều c&oacute; gi&aacute; trị v&agrave; kh&aacute;n giả ri&ecirc;ng. Nhạc của t&ocirc;i c&oacute; nhiều kh&aacute;n giả nghe, fan của t&ocirc;i đ&ocirc;ng chứng tỏ t&ocirc;i được kh&aacute;n giả ủng hộ\", anh nhấn mạnh.</p>\r\n<p>Nam ca sĩ cho biết kh&aacute;n giả vốn quen với m&igrave;nh l&agrave; ch&agrave;ng trai hiền l&agrave;nh, h&aacute;t những bản nhạc pop trữ t&igrave;nh, nhẹ nh&agrave;ng nhưng trong năm 2018, anh sẽ biến h&oacute;a nhiều hơn cả về &acirc;m nhạc v&agrave; phong c&aacute;ch thời trang.</p>\r\n<p>Thậm ch&iacute;, anh c&ograve;n dự định thực hiện c&aacute;c sản phẩm &acirc;m nhạc h&agrave;n l&acirc;m, gi&agrave;u gi&aacute; trị nghệ thuật. \"Chứng minh m&igrave;nh l&agrave; ca sĩ thế n&agrave;o phải bằng sản phẩm &acirc;m nhạc. Thay v&igrave; đ&aacute;p trả nhận x&eacute;t ti&ecirc;u cực bằng lời n&oacute;i, t&ocirc;i muốn thể hiện bằng h&agrave;nh động\", anh cho hay.</p>', 'amnhac1.jpg', '20-05-2024', '16:22:34', 2, 1, 1, 0),
(28, 'Hé lộ lý do khiến nam ca sĩ Hàn tự vẫn ở tuổi 27', 'Tin tức về sự ra đi của Jong Hyun nhóm SHINee khiến làng giải trí Hàn bàng hoàng.', '<p style=\"text-align: justify;\">&nbsp;</p>\r\n<table border=\"0\" width=\"100%\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td rowspan=\"2\" align=\"left\" valign=\"top\" width=\"584\">\r\n<div class=\"colCenter\">\r\n<div>\r\n<div class=\"text-conent\">\r\n<div class=\"baiviet-bailienquan\">&nbsp;</div>\r\n<p>Jong Hyun nh&oacute;m SHINee qua đời tại ph&ograve;ng l&agrave;m việc v&agrave;o ng&agrave;y 18.12 khiến showbiz H&agrave;n r&uacute;ng động. L&yacute; do thực sự vẫn đang được cảnh s&aacute;t điều tra. Tuy nhi&ecirc;n, chị g&aacute;i của Jong Hyun đ&atilde; l&ecirc;n tiếng x&aacute;c nhận em trai tự vẫn.</p>\r\n<p align=\"center\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-19/1513650228-231-he-lo-ly-do-khien-nam-ca-si-han-tu-van-o-tuoi-27-0139_jonghyun-1513599420777-1513649695-width660height371.jpg\" alt=\"H&eacute; lộ l&yacute; do khiến nam ca sĩ H&agrave;n tự vẫn ở tuổi 27 - 1\" /></p>\r\n<p align=\"center\">Nam ca sĩ Jong Hyun qua đời ở tuổi 27</p>\r\n<p>Jong Hyun l&agrave; giọng ca ch&iacute;nh trong nh&oacute;m SHINee. Sau khi anh qua đời, tương lai của nh&oacute;m đang rất bấp b&ecirc;nh. 4 th&agrave;nh vi&ecirc;n c&ograve;n lại gồm Tae Min, Onew, Minho v&agrave; Key. Kh&ocirc;ng chỉ sở hữu chất giọng khỏe khoắn v&agrave; t&igrave;nh cảm, Jong Hyun c&ograve;n nổi tiếng với t&agrave;i năng s&aacute;ng t&aacute;c với 60 b&agrave;i h&aacute;t.</p>\r\n<p>Nh&oacute;m SHINee l&agrave; một trong những t&ecirc;n tuổi của l&agrave;ng nhạc K-Pop. Jong Hyun tham gia tour diễn ở nước ngo&agrave;i c&ugrave;ng với nh&oacute;m v&agrave; trở về H&agrave;n v&agrave;o ng&agrave;y 12.12. Sự ra đi của Jong Hyun ẩn chứa nhiều uẩn kh&uacute;c khiến ai nấy đều b&agrave;ng ho&agrave;ng.</p>\r\n<p>Trang&nbsp;<em>Dispatch</em>&nbsp;H&agrave;n Quốc đ&atilde; c&oacute; cuộc phỏng vấn với một người bạn th&acirc;n của nam ca sĩ. Qua c&acirc;u chuyện chia sẻ của người bạn, c&ocirc;ng ch&uacute;ng hiểu hơn cuộc sống phải chịu đựng nhiều mệt mỏi của Jong Hyun ẩn giấu ph&iacute;a sau lớp vỏ bọc h&agrave;o nho&aacute;ng của một ng&ocirc;i sao thần tượng được triệu người mến mộ.</p>\r\n<p style=\"text-align: center;\" align=\"center\">&nbsp;</p>\r\n<div>\r\n<div><img class=\"news-image\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-19/1513650228-618-he-lo-ly-do-khien-nam-ca-si-han-tu-van-o-tuoi-27-shinee-hn-3-1513649695-width660height363.jpg\" alt=\"H&eacute; lộ l&yacute; do khiến nam ca sĩ H&agrave;n tự vẫn ở tuổi 27 - 2\" />\r\n<div id=\"24h-banner-in-image\">&nbsp;</div>\r\n</div>\r\n</div>\r\n<p>&nbsp;</p>\r\n<p align=\"center\">Nh&oacute;m SHINee tạo dựng được t&ecirc;n tuổi khắp ch&acirc;u &Aacute; trong đ&oacute; Jong Hyun giữ vai tr&ograve; ca sĩ ch&iacute;nh ki&ecirc;m s&aacute;ng t&aacute;c</p>\r\n<div class=\"banner_inread\">&nbsp;</div>\r\n<p>&nbsp;</p>\r\n<p>Người bạn của Jong Hyun nghĩ rằng &acirc;m nhạc l&agrave; điều khiến cậu ấy lu&ocirc;n suy nghĩ khổ sở, rằng muốn l&agrave;m được điều g&igrave; xuất sắc hơn nhưng kh&ocirc;ng thể. &Aacute;p lực về việc phải l&agrave;m thật tốt trong &acirc;m nhạc khiến cậu ấy lu&ocirc;n tự tr&aacute;ch m&igrave;nh.</p>\r\n<p>Đặc biệt, trong tiết lộ của người bạn, Jong Hyun bị một thời gian mất ngủ. Nam ca sĩ kh&ocirc;ng thể n&agrave;o ngủ được nếu thiếu thuốc ngủ. V&agrave;o đầu th&aacute;ng 12 khi hai người gặp nhau, Jong Hyun đ&atilde; n&oacute;i về những điều li&ecirc;n quan tới c&aacute;i chết. Khi đ&oacute; người bạn kh&ocirc;ng nghĩ tới sự lựa chọn ti&ecirc;u cực của Jong Hyun.</p>\r\n<p>Tr&ecirc;n trang c&aacute; nh&acirc;n Instagram của Jong Hyun, b&agrave;i đăng cuối c&ugrave;ng v&agrave;o ng&agrave;y 20.11 năm nay. Anh đăng tải một bức ảnh chụp m&agrave;n h&igrave;nh lời b&agrave;i h&aacute;t<em>&nbsp;Beside You</em>&nbsp;của nh&oacute;m Dear Cloud, trong đ&oacute; c&oacute; những c&acirc;u từ n&oacute;i về sự c&ocirc; đơn: &ldquo;Một m&igrave;nh trong căn ph&ograve;ng tối tăm. Khi m&agrave; bạn c&oacute; thể hối hận, v&igrave; đ&atilde; bu&ocirc;ng tay mọi người. Những tiếng thở d&agrave;i v&agrave; vết thương dai dẳng giờ c&oacute; thể chấm dứt. T&ocirc;i cầu nguyện bạn sẽ kh&ocirc;ng phải chịu đau đớn. T&ocirc;i hy vọng bạn sẽ lu&ocirc;n hạnh ph&uacute;c. Xin đừng cố gắng chịu đựng c&ocirc; đơn trong b&oacute;ng tối. Đừng h&agrave;nh hạ bản th&acirc;n. Đừng h&agrave;nh hạ ch&iacute;nh m&igrave;nh&rdquo;.</p>\r\n<p>Ngay sau c&aacute;i chết của Jong Hyun, to&agrave;n bộ lịch tr&igrave;nh của c&aacute;c nghệ sĩ trực thuộc c&ocirc;ng ty giải tr&iacute; SM được ho&atilde;n lại để tưởng nhớ người qu&aacute; cố.</p>\r\n</div>\r\n</div>\r\n</div>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>', '1513650228-231-he-lo-ly-do-khien-nam-ca-si-han-tu-van-o-tuoi-27-0139_jonghyun-1513599420777-1513649695-width660height371.jpg', '20-05-2024', '21:27:17', 2, 1, 1, 1),
(29, 'Ngô Kiến Huy không sợ bị nghi \"ăn theo\" Sơn Tùng làm MV cổ trang', 'Nam ca sĩ khẳng định, anh không ngại nếu bị so sánh với Sơn Tùng bởi 2 MV \"Lạc giữa nhân gian\" và \"Lạc trôi\" khác nhau về nội dung, hình ảnh.', '<p style=\"text-align: justify;\">Tối 18.12, Ng&ocirc; Kiến Huy đ&atilde; cho ra mắt MV&nbsp;<em>Lạc giữa nh&acirc;n gian</em>&nbsp;mang phong c&aacute;ch cổ trang.</p>\r\n<p style=\"text-align: justify;\">Kh&ocirc;ng chỉ mạnh tay chi tiền tỷ&nbsp;kết hợp c&ugrave;ng đạo diễn Đinh H&agrave; Uy&ecirc;n Thư - người từng ghi dấu với 2 MV&nbsp;cổ trang đ&igrave;nh đ&aacute;m l&agrave;&nbsp;<em>Lạc tr&ocirc;i</em>&nbsp;(Sơn T&ugrave;ng) v&agrave;&nbsp;<em>Sống xa anh chẳng dễ d&agrave;ng</em>&nbsp;(Bảo Anh), bạn trai Khổng T&uacute; Quỳnh c&ograve;n mời 3 c&aacute;i t&ecirc;n nổi tiếng trong l&agrave;ng nhạc l&agrave; Phương Uy&ecirc;n, Đỗ Hiếu v&agrave; Rhymastic s&aacute;ng t&aacute;c v&agrave; phối nhạc cho m&igrave;nh.</p>\r\n<p style=\"text-align: justify;\">Tuy nhi&ecirc;n, ngay từ khi tung teaser đầu ti&ecirc;n, MV của Ng&ocirc; Kiến Huy đ&atilde; được nhiều kh&aacute;n giả so s&aacute;nh với MV&nbsp;<em>Lạc tr&ocirc;i&nbsp;</em>của Sơn T&ugrave;ng, nhất l&agrave; khi&nbsp;tạo h&igrave;nh của 2 nh&acirc;n vật, ca từ v&agrave; t&ecirc;n ca kh&uacute;c đều c&oacute; nhiều điểm tương đồng.&nbsp;</p>\r\n<p style=\"text-align: center;\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-19/1513648900-774-ngo-kien-huy-3-1513646927-width660height440.jpg\" alt=\"Ng&ocirc; Kiến Huy kh&ocirc;ng sợ bị nghi &amp;#34;ăn theo&amp;#34; Sơn T&ugrave;ng l&agrave;m MV cổ trang - 1\" /></p>\r\n<p style=\"text-align: center;\"><em>H&igrave;nh ảnh Ng&ocirc; Kiến Huy trong MV Lạc giữa nh&acirc;n gian kh&aacute; giống với Sơn T&ugrave;ng trong ca kh&uacute;c Lạc tr&ocirc;i.</em></p>\r\n<p style=\"text-align: justify;\">Khi được hỏi về điều n&agrave;y, Ng&ocirc; Kiến Huy b&igrave;nh thản đ&aacute;p trả:&nbsp;<em>&ldquo;Khi nhận được b&agrave;i h&aacute;t Lạc giữa nh&acirc;n gian từ nhạc sĩ Phương Uy&ecirc;n, cả t&ocirc;i v&agrave; &ecirc;-k&iacute;p đều thống nhất MV của b&agrave;i h&aacute;t n&agrave;y sẽ chẳng c&oacute; c&aacute;i n&agrave;o hợp hơn ngo&agrave;i cổ trang. Đ&uacute;ng l&agrave; bản th&acirc;n t&ocirc;i cũng hơi bất ngờ v&igrave; hai b&agrave;i h&aacute;t c&oacute; phần giống nhau ở tựa đề v&agrave; tạo h&igrave;nh nh&acirc;n vật, nhưng t&ocirc;i tự tin khẳng định rằng, MV của m&igrave;nh v&agrave; T&ugrave;ng đều mang một n&eacute;t ri&ecirc;ng.</em></p>\r\n<p style=\"text-align: justify;\"><em>Trong MV của T&ugrave;ng, cậu ấy v&agrave;o vai một vị vua, c&ograve;n t&ocirc;i l&agrave; một kẻ l&atilde;ng kh&aacute;ch phi&ecirc;u bạt giang hồ. Về h&igrave;nh ảnh, th&ocirc;ng điệp c&acirc;u chuyện&nbsp;v&agrave;&nbsp;chất liệu của 2 ca kh&uacute;c đều kh&aacute;c nhau ho&agrave;n to&agrave;n, điểm tương đồng duy nhất ở đ&acirc;y c&oacute; lẽ c&ugrave;ng l&agrave; MV&nbsp;cổ trang do đạo diễn Đinh H&agrave; Uy&ecirc;n Thư thực hiện&rdquo;.</em></p>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<div style=\"text-align: center;\">\r\n<div><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-19/1513648900-693-ngo-kien-huy-1-1513646113-width660height599.jpg\" alt=\"Ng&ocirc; Kiến Huy kh&ocirc;ng sợ bị nghi &amp;#34;ăn theo&amp;#34; Sơn T&ugrave;ng l&agrave;m MV cổ trang - 2\" />\r\n<div id=\"24h-banner-in-image\">&nbsp;</div>\r\n</div>\r\n</div>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<p style=\"text-align: center;\">Ng&ocirc; Kiến Huy kh&ocirc;ng ngại bị so s&aacute;nh với Sơn T&ugrave;ng trong MV mới.</p>\r\n<p style=\"text-align: justify;\">Ch&agrave;ng Bắp cũng cho biết th&ecirc;m, anh kh&ocirc;ng ngại nếu sản phẩm của m&igrave;nh bị so s&aacute;nh với&nbsp;<em>Lạc tr&ocirc;i</em>&nbsp;của Sơn T&ugrave;ng. Bởi ngo&agrave;i đời, cả hai đều l&agrave; bạn b&egrave;, đồng nghiệp th&acirc;n thiết sau khi hợp t&aacute;c trong phim điện ảnh&nbsp;<em>Ch&agrave;ng trai năm ấy</em>&nbsp;v&agrave; chưa bao giờ c&oacute; suy nghĩ ganh đua, tị nạnh người c&ograve;n lại.</p>', 'amnhac3.jpg', '20-05-2024', '21:30:03', 2, 1, 1, 1),
(30, 'Thành viên Big Bang kết hôn với nữ hoàng nội y', 'Sau 3 năm yêu, Tae Yang đã quyết định làm lễ cưới với bạn gái xinh đẹp hơn 2 tuổi.', '<p>C&ocirc;ng ty giải tr&iacute; YG - quản l&yacute; nh&oacute;m&nbsp;<strong>BigBang&nbsp;</strong>- cho biết nam ca sĩ Tae Yang sẽ kết h&ocirc;n với bạn g&aacute;i Min Hyo Rin v&agrave;o th&aacute;ng 2 năm sau. Tiệc cưới được tổ chức&nbsp;trước khi&nbsp;nam ca sĩ thực hiện nghĩa vụ qu&acirc;n sự.</p>\r\n<p align=\"center\">&nbsp;</p>\r\n<div>\r\n<div><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-18/1513591057-313-thanh-vien-dau-tien-nhom-big-bang-ket-hon-voi-nu-hoang-noi-y-318fb5ad-eba6-4a6e-808d-80220ff08101-1513589537-width800height533.jpg\" alt=\"Th&agrave;nh vi&ecirc;n Big Bang kết h&ocirc;n với nữ ho&agrave;ng nội y - 1\" />\r\n<div id=\"24h-banner-in-image\">&nbsp;</div>\r\n</div>\r\n</div>\r\n<p>&nbsp;</p>\r\n<p align=\"center\">Tae Yang c&ugrave;ng Min Hyo Rin đi tới con đường hạnh ph&uacute;c</p>\r\n<p>Tr&ecirc;n trang c&aacute; nh&acirc;n, Tae Yang viết gửi tới người h&acirc;m mộ: \"Xin ch&agrave;o mọi người, t&ocirc;i l&agrave; Tae Yang đ&acirc;y. H&ocirc;m nay t&ocirc;i muốn chia sẻ với c&aacute;c bạn về cột mốc quan trọng v&agrave; hạnh ph&uacute;c nhất của cuộc đời m&igrave;nh. T&ocirc;i đ&atilde; nguyện d&agrave;nh cả cuộc đời của m&igrave;nh cho Min Hyo Rin, người đ&atilde; lu&ocirc;n b&ecirc;n cạnh t&ocirc;i trong suốt 4 năm nay. Suốt trong những khoảng thời gian kh&oacute; khăn hay hạnh ph&uacute;c, c&ocirc; ấy vẫn lu&ocirc;n lu&ocirc;n tin t&ocirc;i v&agrave; b&acirc;y giờ t&ocirc;i muốn c&ugrave;ng c&ocirc; ấy x&acirc;y dựng một cuộc sống hạnh ph&uacute;c c&ugrave;ng với nhau.</p>\r\n<p>Gửi đến tất cả fan của t&ocirc;i, những người lu&ocirc;n xem t&ocirc;i l&agrave; một phần&nbsp;gia đ&igrave;nh của c&aacute;c bạn, gửi đến c&aacute;c th&agrave;nh vi&ecirc;n của&nbsp;<strong>BIGBANG&nbsp;</strong>của t&ocirc;i, gửi đến to&agrave;n bộ YG Family v&agrave; những người m&agrave; t&ocirc;i đ&atilde; từng gặp v&agrave; l&agrave;m việc c&ugrave;ng. T&ocirc;i chỉ muốn n&oacute;i c&aacute;m ơn mọi người.&nbsp;T&ocirc;i sẽ tiếp tục cuộc sống của m&igrave;nh thật tr&aacute;ch nhiệm v&agrave; khi&ecirc;m tốn hơn. T&ocirc;i mong rằng c&aacute;c bạn vẫn sẽ ủng hộ v&agrave; gửi thật nhiều lời ch&uacute;c đến ch&uacute;ng t&ocirc;i trong tương lai\".</p>\r\n<p align=\"center\"><img class=\"news-image\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-12-18/1513591058-448-thanh-vien-dau-tien-nhom-big-bang-ket-hon-voi-nu-hoang-noi-y-8-1513590102-width535height700.jpg\" alt=\"Th&agrave;nh vi&ecirc;n Big Bang kết h&ocirc;n với nữ ho&agrave;ng nội y - 3\" /></p>\r\n<p align=\"center\">Vẻ gợi cảm vợ sắp cưới của Tae Yang</p>\r\n<p>Taeyang v&agrave; nữ ho&agrave;ng nội y Min Hyo Rin y&ecirc;u nhau năm 2014 v&agrave; c&ocirc;ng khai hẹn h&ograve; từ th&aacute;ng 6. 2015. Cả hai lu&ocirc;n g&igrave;n giữ h&igrave;nh ảnh cho nhau trong khoảng thời gian y&ecirc;u. Hơn Tae Yang 2 tuổi, Min Hyo Rin được biết đến l&agrave; người mẫu ki&ecirc;m diễn vi&ecirc;n nổi tiếng của nhiều phim như&nbsp;<em>Sunny, Si&ecirc;u trộm ho&agrave;ng cung..</em>. Trong khi đ&oacute; Tae Yang được cả ch&acirc;u &Aacute; v&agrave; thế giới biết đến với tư c&aacute;ch th&agrave;nh vi&ecirc;n&nbsp;<strong>nh&oacute;m nhạc BigBang</strong>&nbsp;</p>', 'TaeyangMinHyoRin.jpg', '20-05-2024', '21:33:46', 2, 1, 1, 1);

